(52, 'avalara_q3-2023_noAvaTax', 'marketing', 'en_US', 'Automatically calculate VAT in real time', 'Take the effort out of determining tax rates and sell confidently across borders with automated tax management from Avalara AvaTax including built-in VAT calculation when you sell into or across the EU and UK. Save time and stay compliant when you let Avalara do the heavy lifting.', '{}', 'pending', 'woocommerce.com', '2024-06-24 12:29:00', NULL, 0, 'plain', '', 0, 0, 'info'),
(53, 'woo-activation-survey-blockers-22AUG23', 'info', 'en_US', 'How can we help you get that first sale?', 'Your feedback is vital. Please take a minute to share your experience of setting up your new store and whether anything is preventing you from making those first few sales. Together, we can make Woo even better!', '{}', 'unactioned', 'woocommerce.com', '2024-06-25 04:59:51', NULL, 0, 'plain', '', 0, 1, 'info'),
(54, 'woocommerce-usermeta-Sept2023-productvendors', 'update', 'en_US', 'Your store requires a security update', '<strong>Your shop needs an update to address a vulnerability in WooCommerce.</strong> The fix was released on Sept 15. Please update WooCommerce to the latest version immediately. <a href=\"https://developer.woocommerce.com/2023/09/16/woocommerce-vulnerability-reintroduced-from-7-0-1/\" />Read our developer update</a> for more information.', '{}', 'pending', 'woocommerce.com', '2024-06-24 12:29:00', NULL, 0, 'plain', '', 0, 0, 'info'),
(55, 'woocommerce-STRIPE-Oct-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe Gateway', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce Stripe Gateway</strong>. The fix for this vulnerability was released on October 17. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2024-06-24 12:29:00', NULL, 0, 'plain', '', 0, 0, 'info'),
(56, 'amazon-mcf-reviews-2023-12-07', 'marketing', 'en_US', 'Enjoying Amazon MCF for WooCommerce?', 'We\'re Never Settle, the developers behind Amazon MCF for WooCommerce, and would be deeply honored to have your review. Reviews help immensely as other users can learn how MCF can solve their needs too! Not happy or need help? Please reach out for support and wed love to make things right!', '{}', 'pending', 'woocommerce.com', '2024-06-24 12:29:01', NULL, 0, 'plain', '', 0, 0, 'info'),
(57, 'remove_estimated_deposits_2024', 'marketing', 'en_US', 'Estimated deposits are going away', 'To provide more accurate deposit information and support the expansion of instant deposits, estimated deposit details will no longer be available in WooPayments. We recommend upgrading to the latest version of WooPayments for more detailed balance status information.', '{}', 'pending', 'woocommerce.com', '2024-06-24 12:29:01', NULL, 0, 'plain', '', 0, 0, 'info'),
(58, 'woocommerce-payfast-may-2024-update-needed', 'update', 'en_US', 'An important update is required for the Payfast Payment Gateway', '<strong>Please update to the latest version of the Payfast Payment Gateway for WooCommerce immediately to address a security vulnerability</strong>. This update is required to ensure that your transactions continue to process.', '{}', 'pending', 'woocommerce.com', '2024-06-24 12:29:01', NULL, 0, 'plain', '', 0, 0, 'info'),
(59, 'checkout_block_update_july_2024', 'marketing', 'en_US', 'UPDATE: The checkout block is getting an upgrade!', 'Were improving the checkout block in 2024, starting with WooCommerce 9.0, launching on June 18! The latest release includes streamlined order summary with sticky functionality, better address form layout, and other enhancements to boost conversion.', '{}', 'pending', 'woocommerce.com', '2024-06-24 12:29:01', NULL, 0, 'plain', '', 0, 0, 'info'),
